switch (empType)
{
    case ENGINEER:
         return m_basicSalary;
    case SALESMAN:
        return m_basicSalary + m_commission;
    case MANAGER:
        return 2 * m_basicSalary;
    default:
        throw new Exception("no such employee type!");
}

